import React, { useState } from 'react'
import CourseDataService from "../service/CourseDataService";

const AddMaterialForm = props => {
    const initialFormState = { idMat: null, name: '', keyName: '', amountToRequest:'', presentation:'',measurementType:'', dimensionsMaterial: '',  quantityPresentation: '', price:'', supplier:'' }
    const [ material, setMaterial ] = useState(initialFormState)

    const handleInputChange = event => {
        const { name, value } = event.target

        setMaterial({ ...material, [name]: value })
    }

    return (
        <form
            onSubmit={event => {
                event.preventDefault()
                if (!material.name || !material.keyName) return

                else{
                    CourseDataService.createCourse(material)
                        .then(() => this.props.history.push('/'))
                }
            }}
        >
            <label>ID</label>
            <input type="text" name="name" value={material.idMat} onChange={handleInputChange} />
            <label>Name</label>
            <input type="text" name="name" value={material.name} onChange={handleInputChange} />
            <label>Key</label>
            <input type="text" name="keyName" value={material.keyName} onChange={handleInputChange} />
            <label>Presentation</label>
            <input type="text" name="presentation" value={material.presentation} onChange={handleInputChange} />
            <label>Measurement</label>
            <input type="text" name="measurementType" value={material.measurementType} onChange={handleInputChange} />
            <label>Dimensions</label>
            <input type="text" name="dimensionsMaterial" value={material.dimensionsMaterial} onChange={handleInputChange} />
            <label>Quantity - Presentation</label>
            <input type="text" name="quantityPresentation" value={material.quantityPresentation} onChange={handleInputChange} />
            <label>Total Amount</label>
            <input type="text" name="amountToRequest" value={material.amountToRequest} onChange={handleInputChange} />
            <label>Price</label>
            <input type="text" name="price" value={material.price} onChange={handleInputChange} />
            <label>Supplier</label>
            <input type="text" name="supplier" value={material.supplier} onChange={handleInputChange} />
            <button>Add new material</button>
        </form>
    )
}

export default AddMaterialForm
