import React, { Component } from 'react'
import CourseDataService from "../service/CourseDataService";
import InputForm from "../InputForm";
import {withRouter} from 'react-router-dom';

class EditMaterialForm extends Component {

    constructor(props) {
        super(props)
        this.state = {
            name: '',
            keyName: '',
            specification: {
                presentation: '',
                measurementType: '',
                dimensions: '',
                quantityPresentation: '',
                amountToRequest: '',
                price: '',
                supplier: ''
            },
            message: null
        }
        this.addCourseClicked = this.addCourseClicked.bind(this)
        this.refreshCourses = this.refreshCourses.bind(this)
    }

    componentDidMount() {
        this.refreshCourses();
    }

    refreshCourses() {
        CourseDataService.retrieveCourse()
            .then(
                response => {
                    //console.log(response);
                    this.setState({ materials: response.data })
                }
            )
    }



    addCourseClicked() {
        console.log(this.state)
        CourseDataService.createCourse(this.state)
            .then(
                response => {
                    //console.log(response);
                    this.props.history.push(`/`)
                    this.setState({ material: response.data })
                }
            )
    }

    handleChange = event => {
        const { value, name } = event.target;
        this.setState({
            ...this.state,
            [name]: value
        });
        console.log(this.state)
    };

    handleSpecificationChange = event => {
        const { value, name } = event.target;
        const {specification} = this.state;
        specification[name]=value;
        this.setState({
            ...this.state,
            specification
        });
        console.log(this.state)
    };

    render() {
        return (
            <div className="container">
                <h3>Update Material</h3>
                <div className="container">
                    <table className="table">
                        <InputForm
                            name="name"
                            label="name"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.name}
                        />
                        <InputForm
                            name="keyName"
                            label="keyName"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.keyName}
                        />
                        <InputForm
                            name="presentation"
                            label="presentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.presentation}
                        />
                        <InputForm
                            name="measurementType"
                            label="measurementType"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.measurementType}
                        />
                        <InputForm
                            name="dimensions"
                            label="dimensions"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.dimensions}
                        />
                        <InputForm
                            name="quantityPresentation"
                            label="quantityPresentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.quantityPresentation}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="price"
                            label="price"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.price}
                        />
                        <InputForm
                            name="supplier"
                            label="supplier"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.supplier}
                        />
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addCourseClicked}>Update</button>
                    </div>
                </div>
            </div>
        )
    }

}

export default withRouter (EditMaterialForm);




