import axios from 'axios'

const COURSE_API_URL = 'http://localhost:8080'
const INSTRUCTOR_API_URL = `${COURSE_API_URL}/material/`

export class CourseDataService {

    retrieveAllCourses() {
        //console.log('executed service')
        return axios.get(`${INSTRUCTOR_API_URL}/`);
    }

    retrieveCourse(id) {
        //console.log('executed service')
        return axios.get(`${INSTRUCTOR_API_URL}/DETAILS/${id}`);
    }

    deleteCourse(id) {
        //console.log('executed service')
        return axios.delete(`${INSTRUCTOR_API_URL}/${id}`);
    }

    updateCourse(id) {
        //console.log('executed service')
        return axios.put(`${INSTRUCTOR_API_URL}/${id}`);
    }

    createCourse() {
        //console.log('executed service')
        return axios.post(`http://localhost:8080/material/`);
    }

    async findById(resource, id) {
        const baseUrl = environment.api[resource];
        const requestUrl = `${baseUrl}/${id}`;
        return axios.get(requestUrl);
    }

    async findAll(resource) {
        const baseUrl = environment.api[resource];
        return axios.get(baseUrl);
    }

    async create(resource, data) {
        const baseUrl = environment.api[resource];
        return axios.post(baseUrl, data);
    }

    async update(resource, id, data) {
        const baseUrl = environment.api[resource];
        const requestUrl = `${baseUrl}/${id}`;
        return axios.put(requestUrl, data);
    }
};

export default new CourseDataService();
