import React, {Component, useState} from 'react'
import CourseDataService from "../service/CourseDataService";

class AddMaterialForm extends Component{
    state = {
        idMat: null,
        name: '',
        keyName: '',
        specification: {
            presentation: '',
            measurementType: '',
            dimensions: '',
            quantityPresentation: '',
            amountToRequest: '',
            price: '',
            supplier: ''
        }
    }

    componentDidMount() {
        const [material, setMaterial] = useState(initialFormState)

        const handleInputChange = event => {
            const {name, value} = event.target
            setMaterial({...material, [name]: value})
        }
    }

    render(){
        return (
            <form
                addMaterial = {
                    CourseDataService.createCourse(material)
                        .then(
                            response => {
                                this.setState({ message: `added` })
                                this.refreshCourses()
                            }
                        )
                }
                onSubmit={event => {
                    const payload = { material };
                    CourseDataService.createCourse(material);
//                    .then(() => this.props.history.push('/'))
                }}
            >
                <label>Name</label>
                <input type="text" name="name" field={material.name} onChange={handleInputChange} />
                <label>Key</label>
                <input type="text" name="keyName" field={material.keyName} onChange={handleInputChange} />
                <label>Presentation</label>
                <input type="text" name="presentation" field={material.specification.presentation} onChange={handleInputChange} />
                <label>Measurement</label>
                <input type="text" name="measurementType" field={material.specification.measurementType} onChange={handleInputChange} />
                <label>Dimensions</label>
                <input type="text" name="dimensions" field={material.specification.dimensions} onChange={handleInputChange} />
                <label>Quantity - Presentation</label>
                <input type="text" name="quantityPresentation" field={material.specification.quantityPresentation} onChange={handleInputChange} />
                <label>Total Amount</label>
                <input type="text" name="amountToRequest" field={material.specification.amountToRequest} onChange={handleInputChange} />
                <label>Price</label>
                <input type="text" name="price" field={material.specification.price} onChange={handleInputChange} />
                <label>Supplier</label>
                <input type="text" name="supplier" field={material.specification.supplier} onChange={handleInputChange} />
                <td><button className="btn btn-warning" onClick={() => this.addMaterial (material.idMat)}>Add new material</button></td>
            </form>
        )
    }


}


export default AddMaterialForm
