import React, { Component } from 'react'
import CourseDataService from "../service/CourseDataService";
import InputForm from "../InputForm";

class EditMaterialForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            [name]: value
//            const [material, setMaterial] = useState (props.currentMaterial)
        }
        this.addCourseClicked = this.addCourseClicked.bind(this)
        this.refreshCourses = this.refreshCourses.bind(this)
    }

    useEffect () {
        setMaterial(props.currentMaterial)
    }

    handleInputChange = event => {
        const { name, value } = event.target
        setMaterial({ ...material, [name]: value })
    }

    render() {
        return (
            <div className="container">
                <h3>Add Materials</h3>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <InputForm
                            name="name"
                            label="name"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.name}
                        />
                        <InputForm
                            name="keyName"
                            label="keyName"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.keyName}
                        />
                        <InputForm
                            name="presentation"
                            label="presentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.presentation}
                        />
                        <InputForm
                            name="measurementType"
                            label="measurementType"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.measurementType}
                        />
                        <InputForm
                            name="dimensions"
                            label="dimensions"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.dimensions}
                        />
                        <InputForm
                            name="quantityPresentation"
                            label="quantityPresentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.quantityPresentation}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="price"
                            label="price"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.price}
                        />
                        <InputForm
                            name="supplier"
                            label="supplier"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.supplier}
                        />
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addCourseClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }

}

export default EditMaterialForm




