import axios from 'axios'

const COURSE_API_URL = 'http://localhost:8080'
const INSTRUCTOR_API_URL = `${COURSE_API_URL}/material/`

export class CourseDataService {

    retrieveAllCourses() {
        //console.log('executed service')
        return axios.get(`${INSTRUCTOR_API_URL}/`);
    }

    retrieveCourse(id) {
        //console.log('executed service')

        /*
        axios.get(`${INSTRUCTOR_API_URL}/DETAILS/${id}`).then(res =>{
            console.log(res.data.type)
            return res.data
        })
        ;

        return axios.get(url).then(response => {
    // returning the data here allows the caller to get it through another .then(...)
    return response.data
  })
        */
        return axios.get(`${INSTRUCTOR_API_URL}/${id}`).then (response => {
            return response
        })
    }

    deleteCourse(id) {
        //console.log('executed service')
        return axios.delete(`${INSTRUCTOR_API_URL}/${id}`);
    }

    updateCourse(id, material) {
        //console.log('executed service')
        return axios.put(`${INSTRUCTOR_API_URL}/${id}`);
    }

    createCourse(material) {
        //console.log('executed service')
        return axios.post(`http://localhost:8080/material/`, material);
    }
};

export default new CourseDataService();
