/*
import React, { useState, Fragment } from 'react'
import AddMaterialForm from './forms/AddMaterialForm'
import EditMaterialForm from './forms/EditMaterialForm'
import MaterialTable from './tables/MaterialTable'

const App = () => {
  // Data
  const materialsData = [
    {id: 1, name: "wood", keyName: "red wood",amountToRequest: 12, presentation: "packs", measurementType: "in",dimensions: "43x42x12", quantityPresentation: 1, price: 12.33, supplier: "provedor 1"     }
  ]

  const initialFormState = { id: null, name: '', keyName: '', amountToRequest:'', presentation:'',measurementType:'', dimensionsMaterial: '',  quantityPresentation: '', price:'', supplier:'' }

  // Setting state
  const [ materials, setMaterials ] = useState(materialsData)
  const [ currentMaterial, setCurrentMaterial ] = useState(initialFormState)
  const [ editing, setEditing ] = useState(false)

  // CRUD operations
  const addMaterial = material => {
    material.id = materials.length + 1
    setMaterials([ ...materials, material ])
  }

  const deleteMaterial = id => {
    setEditing(false)

    setMaterials(materials.filter(material => material.id !== id))
  }

  const updateMaterial = (id, updateMaterial) => {
    setEditing(false)

    setMaterials(materials.map(material => (material.id === id ? updateMaterial : material)))
  }

  const editRow = material => {
    setEditing(true)
    setCurrentMaterial({ id: material.id, name: material.name, keyName: material.keyName, amountToRequest: material.amount, presentation: material.presentation,measurementType: material.measurementType, dimensionsMaterial: material.dimensionsMaterial,  quantityPresentation: material.quantityPresentation, price: material.price, supplier:material.supplier})
  }

  return (
      <div className="container">
        <h1>CRUD App</h1>
        <div className="flex-row">
          <div className="flex-large">
            {editing ? (
                <Fragment>
                  <h2>Edit Material</h2>
                  <EditMaterialForm
                      editing={editing}
                      setEditing={setEditing}
                      currentMaterial={currentMaterial}
                      updateMaterial={updateMaterial()}
                  />
                </Fragment>
            ) : (
                <Fragment>
                  <h2>Add Material</h2>
                  <AddMaterialForm addMaterial={addMaterial} />
                </Fragment>
            )}
          </div>
          <div className="flex-large">
            <h2>View Materials</h2>
            <MaterialTable materials={materials} editRow={editRow} deleteMaterial={deleteMaterial} />
          </div>
        </div>
      </div>
  )
}

export default App

