                            label="keyName"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.keyName}
                        />
                        <InputForm
                            name="presentation"
                            label="presentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.presentation}
                        />
                        <InputForm
                            name="measurementType"
                            label="measurementType"
                            type="text"
