import React, { Component } from 'react'
import CourseDataService from "../service/CourseDataService";
import InputForm from "../InputForm";
import {withRouter} from "react-router-dom";

class EditMaterialForm extends Component {

    stateMat (){
        const id = this.props.match.params.id;

        CourseDataService.retrieveCourse(id)
            .then(
                response => {
                    console.log(response);
                    this.setState({ material: response.data })
                }
            )
    }

    constructor(props) {
        super(props)
        this.state = this.stateMat();

        this.addCourseClicked = this.addCourseClicked.bind(this)
    }

    componentDidMount() {
        const {
            match: { params }
        } = this.props;
    }

    /*
    refreshCourses() {
        CourseDataService.retrieveCourse(this.props.match.params.id)
            .then(
                response => {
                    console.log(response);
                    console.log(response.data.name);
                    console.log(response.data)
                    this.setState({ materials: response.data })
                }
                )
    }*/

    addCourseClicked() {
        CourseDataService.updateCourse(this.props.match.params.id, this.state)
            .then(
                response => {
                    console.log("update " + response);
                    this.props.history.push(`/`)
                    this.setState({ material: response.data })
                }
            )
    }

    handleChange = event => {
        const { value, name } = event.target;
        //setMaterial({...material, [name]: value})
        /*
        this.setState({
            ...this.state,
            [name]: value
        });*/
        console.log(this.state)
    };

    handleSpecificationChange = event => {
        const { value, name } = event.target;
        const {specification} = this.state;
        specification[name]=value;
        this.setState({
            ...this.state,
            specification
        });
    };


    render() {
        return (
            <div className="container">
                <h3>Add Materials</h3>
               // {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <InputForm
                            name="name"
                            label="name"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.name}
                        />
                        <InputForm
                            name="keyName"
                            label="keyName"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.keyName}
                        />
                        <InputForm
                            name="presentation"
                            label="presentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.presentation}
                        />
                        <InputForm
                            name="measurementType"
                            label="measurementType"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.measurementType}
                        />
                        <InputForm
                            name="dimensions"
                            label="dimensions"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.dimensions}
                        />
                        <InputForm
                            name="quantityPresentation"
                            label="quantityPresentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.quantityPresentation}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="price"
                            label="price"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.price}
                        />
                        <InputForm
                            name="supplier"
                            label="supplier"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.supplier}
                        />
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addCourseClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter (EditMaterialForm)







