import React, { Component } from 'react'
import CourseDataService from "../service/CourseDataService";
import InputForm from "../InputForm";
import {withRouter} from "react-router-dom";

class EditMaterialForm extends Component {

    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            name: '',
            keyName: '',
            specification: {
                presentation: '',
                measurementType: '',
                dimensions: '',
                quantityPresentation: '',
                amountToRequest: '',
                price: '',
                supplier: ''
            }
        }
         this.onSubmit = this.onSubmit.bind(this)
    }

    componentDidMount() {
        console.log(this.state.id)
        CourseDataService.retrieveCourse(this.state.id).then(
            response=> this.setState({
                name : response.name,
                keyName: response.keyName,
                specification: response.specification
                /*
                specification: {
                    presentation: response.presentation,
                    measurementType: response.measurementType,
                    dimensions: response.dimensions,
                    quantityPresentation: response.quantityPresentation,
                    amountToRequest: response.amountToRequest,
                    price: response.price,
                    supplier: response.supplier
                }*/
            })
        )
    }

    onSubmit(values){
        const material = {
            id: this.state.id,
            description: values.description,
            name: values.name,
            keyName: values.keyName,
            specification:values.specification
        }
        console.log("VALUE st NAME : " + this.state.name)
        console.log("VALUE NAME : " + this.name)
        console.log("VALUE pres : " + this.state.presentation)
        console.log("VALUE pres spec : " + this.state.specification.presentation)

        CourseDataService.updateCourse(this.state.id, material).then(
            ()=>this.props.history.push('/')
        )
        console.log("values    "+values);
    }

    handleChange = event => {
        const { value, name } = event.target;
        this.setState({
            ...this.state,
            [name]: value
        });
        console.log(this.state)
    };

    render() {
        console.log(this.state.name)
        console.log("STAE ---- " + this.state.values)
        console.log("KEY NAME " + this.state.keyName)
        console.log(this.state.specification)
        console.log(this.state.specification.dimensions)
        console.log(this.state.specification.quantityPresentation)
        console.log(this.state.specification.amountToRequest)
        return (
            <div className="container">
                <h3>Add Materials</h3>
                <div className="container">
                    <table className="table">
                        <InputForm
                            name="name"
                            label="name"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.state.name}
                        />
                        <InputForm
                            name="keyName"
                            label="keyName"
                            type="text"
                            handleChange={this.state.handleChange}
                            value={this.state.keyName}
                        />
                        <InputForm
                            name="presentation"
                            label="presentation"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.state.specification.presentation}
                        />
                        <InputForm
                            name="measurementType"
                            label="measurementType"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.state.specification.measurementType}
                        />
                        <InputForm
                            name="dimensions"
                            label="dimensions"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.state.specification.dimensions}
                        />
                        <InputForm
                            name="quantityPresentation"
                            label="quantityPresentation"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.state.specification.quantityPresentation}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.state.specification.amountToRequest}
                        />
                        <InputForm
                            name="price"
                            label="price"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.state.specification.price}
                        />
                        <InputForm
                            name="supplier"
                            label="supplier"
                            type="text"
                            handleChange={this.handleSpecificationChange}
                            value={this.state.specification.supplier}
                        />
                    </table>
                    <div className="row">
                        <button className="btn btn-success" type="submit">Save</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter (EditMaterialForm)







