import React, { Component } from 'react'
import CourseDataService from "../service/CourseDataService";
import InputForm from "../InputForm";

class AddMaterialForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            idMat: null,
            name: '',
            keyName: '',
            specification: {
                presentation: '',
                measurementType: '',
                dimensions: '',
                quantityPresentation: '',
                amountToRequest: '',
                price: '',
                supplier: ''
            },
            message: null
        }
        this.addCourseClicked = this.addCourseClicked.bind(this)
        this.refreshCourses = this.refreshCourses.bind(this)
    }

    componentDidMount() {
        this.refreshCourses();
    }

    refreshCourses() {
        CourseDataService.retrieveAllCourses()
            .then(
                response => {
                    //console.log(response);
                    this.props.history.push(`/material`)
                    this.setState({ materials: response.data })
                }
            )
    }

    addCourseClicked() {
        CourseDataService.createCourse()
    }

    render() {
        return (
            <div className="container">
                <h3>Add Materials</h3>
                {this.state.message && <div class="alert alert-success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                        <tr>
                            <th>Name</th>
                            <th>Key Name</th>
                            <th>Presentation</th>
                            <th>Measurement</th>
                            <th>Dimensions</th>
                            <th>Quantity - Presentation</th>
                            <th>Total Amount</th>
                            <th>Price</th>
                            <th>Supplier</th>
                            <th>Update</th>
                            <th>Delete</th>
                        </tr>
                        </thead>
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.amountToRequest}
                        />
                        <InputForm
                            name="amountToRequest"
                            label="amountToRequest"
                            type="text"
                            handleChange={this.handleChange}
                            value={this.amountToRequest}
                        />
                    </table>
                    <div className="row">
                        <button className="btn btn-success" onClick={this.addCourseClicked}>Add</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default AddMaterialForm





