import InputForm from "../InputForm";
import React, { Component } from "react";
//import AddressForm from "../address-form/address-form.component";
//import DataService from "../../services/data.service";
import {Redirect} from "react-router-dom";
import specificationForm from "../specificationForm";
import {CourseDataService} from "../service/CourseDataService";
import MaterialApp from "../MaterialApp";

class AddMaterialForm extends Component {
    state={
        idMat: null,
        name: '',
        keyName: '',
        specification: {
            presentation: '',
            measurementType: '',
            dimensions: '',
            quantityPresentation: '',
            amountToRequest: '',
            price: '',
            supplier: ''
        }
    };

    componentDidMount() {
        const {
            match: { params }
        } = this.props;
        const id = params.id;
        const isUpdate = !isNaN(id);
        if (isUpdate) {
            this.setState({
                id: 4,
                address: {
                    street: "Colegio Militar 4700",
                    city: "Chihuahua",
                    state: "CUU",
                    country: "Mexico",
                    zip: "90210",

                    latitude: -26.0,
                    longitude: 108.0,
                    imageUrl: "http://www.mock.com/image.jpg",
                    area: "FINANCE",
                    additionalInformation: "3rd Floor 2nd Office"
                },
                destinationType: "Office"
            });
            /*
            DataService.findById("destinatinos", id)
                .then(({ data: { id, address, destinationType } }) => {
                    console.log(address);
                    this.setState({ id, address, destinationType });
                })
                .catch(e =>
                    console.error(`An error occurred while fetching Destination ${id}`, e)
                );*/
        }
        this.setState({ isUpdate });
    }

    handleSpecificationChange = event => {
        const {value, name} = event.target;
        const {specification} = this.state;
        specification[name] = value;
        this.setState({specification});
    };

    handleSubmit = e => {
        e.preventDefault();
        // do something with this
        const { material, specification, id } = this.state;
        const payload = { material, specification };
        const formAction = this.state.isUpdate
            ? CourseDataService.update("materials", id, payload)
            : CourseDataService.create("materials", payload);
        formAction.then().catch(e => console.debug(e));
        return <Redirect to= {`/material/`}/>;
    };

    render() {
        return (
            <div>
                <h1>{`${this.state.isUpdate ? "Update" : "New"} Destination`}</h1>
                <form>
                    <h4>Address Details</h4>
                    <specificationForm
                        handleChange={this.handleSpecificationChange}
                        {...this.state}
                    />
                    <InputForm
                        name="amountToRequest"
                        label="amountToRequest"
                        type="text"
                        handleChange={handleChange}
                        value={specification.amountToRequest}
                    />
                    <button onClick={this.handleSubmit} className="btn btn-primary" >
                        Submit
                    </button>
                </form>
            </div>
        );
    }
}
export default AddMaterialForm
