import React from "react";
import InputForm from "../InputForm";

const AddMaterialForm = ({ handleChange, ...props }) => {
    const { material } = props;
    return (
        <div id="material-form" className="form-group">
            <div className="form-group">
                <InputForm
                    name="street"
                    label="Street"
                    type="text"
                    handleChange={handleChange}
                    value={material.name}
                />
            </div>
            <div className="form-row from-group">
                <InputForm
                    name="city"
                    label="City"
                    type="text"
                    handleChange={handleChange}
                    value={material.keyName}
                    className="col"
                />
                <InputForm
                    name="state"
                    label="State"
                    type="text"
                    handleChange={handleChange}
                    value={material.specification.presentation}
                    className="col"
                />
                <InputForm
                    name="country"
                    label="Country"
                    type="text"
                    handleChange={handleChange}
                    value={material.specification.presentation}
                    className="col"
                />
            </div>

            <div className="form-row from-group">
                <FormInput
                    name="zip"
                    label="ZIP Code"
                    type="text"
                    handleChange={handleChange}
                    value={address.zip}
                    className="col"
                />
                <FormInput
                    name="area"
                    label="Area"
                    type="text"
                    handleChange={handleChange}
                    value={address.area}
                    className="col"
                />
            </div>
            <FormInput
                name="additionalInformation"
                label="Additional Information"
                type="text"
                handleChange={handleChange}
                value={address.additionalInformation}
            />
        </div>
    );
};

export default AddMaterialForm;



