import React, { Component } from 'react';
import CourseDataService from "./service/CourseDataService";

class MaterialTable extends Component {

    constructor(props) {
    super(props)
    this.state = {
        materials: []
    }
    this.refreshCourses = this.refreshCourses.bind(this)
    }

    componentDidMount() {
        this.refreshCourses();
    }

    refreshCourses() {
        CourseDataService.retrieveAllCourses(1)//HARDCODED
            .then(
                response => {
                    console.log(response);
                    this.setState({ materials: response.data })
                }
            )
    }
    render() {
        return (
            <table>
                <thead>
                <tr>
                    <th>Name</th>
                    <th>Key</th>
                    <th>Presentation</th>
                    <th>Measurement</th>
                    <th>Dimensions</th>
                    <th>Quantity - Presentation</th>
                    <th>Total Amount</th>
                    <th>Price</th>
                    <th>Supplier</th>
                </tr>
                </thead>
                <tbody>
                {
                    this.state.materials.map(
                        material =>
                            <tr key={material.id}>
                                <td>{material.name}</td>
                                <td>{material.keyName}</td>
                                <td>{material.amountToRequest}</td>
                                <td>{material.presentation}</td>
                                <td>{material.measurementType}</td>
                                <td>{material.dimensionsMaterial}</td>
                                <td>{material.quantityPresentation}</td>
                                <td>{material.price}</td>
                                <td>{material.supplier}</td>
                            </tr>
                    )
                }
                </tbody>
            </table>

        )
    }
}

export default MaterialTable
