import React, {Component, useState} from 'react'
import CourseDataService from "../service/CourseDataService";

class AddMaterialForm extends Component {
    constructor(props) {
        super(props);
        this.state ={
            idMat: null,
            name: '',
            keyName: '',
            specification: {
                presentation: '',
                measurementType: '',
                dimensions: '',
                quantityPresentation: '',
                amountToRequest: '',
                price: '',
                supplier: ''
            }
        }
    }
}


